package com.roxy.messengerapp.fragments; // Проверь пакетimport android.content.Intent;import android.os.Bundle;import android.text.TextUtils;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import androidx.navigation.Navigation;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.AuthResult;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.auth.FirebaseUser;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.roxy.messengerapp.R;import com.roxy.messengerapp.activities.MainActivity;import java.util.HashMap;public class RegisterFragment extends Fragment {    EditText username, email, password;    Button btn_register;    TextView tv_login;    FirebaseAuth auth;    DatabaseReference reference;    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {        // Надуваем макет фрагмента        return inflater.inflate(R.layout.fragment_register, container, false);    }    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        // Инициализация View        username = view.findViewById(R.id.username);        email = view.findViewById(R.id.email);        password = view.findViewById(R.id.password);        btn_register = view.findViewById(R.id.btn_register);        tv_login = view.findViewById(R.id.tv_login);        auth = FirebaseAuth.getInstance();        tv_login.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                // Идем по стрелке, которую создали в шаге 2                Navigation.findNavController(v).navigate(R.id.action_register_to_login);            }        });        btn_register.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String txt_username = username.getText().toString();                String txt_email = email.getText().toString();                String txt_password = password.getText().toString();                if (TextUtils.isEmpty(txt_username) || TextUtils.isEmpty(txt_email) || TextUtils.isEmpty(txt_password)){                    Toast.makeText(getContext(), "Заполните все поля", Toast.LENGTH_SHORT).show();                } else if (txt_password.length() < 6){                    Toast.makeText(getContext(), "Пароль должен быть минимум 6 символов", Toast.LENGTH_SHORT).show();                } else {                    register(txt_username, txt_email, txt_password);                }            }        });    }    private void register(final String username, String email, String password){        auth.createUserWithEmailAndPassword(email, password)                .addOnCompleteListener(new OnCompleteListener<AuthResult>() {                    @Override                    public void onComplete(@NonNull Task<AuthResult> task) {                        if (task.isSuccessful()){                            FirebaseUser firebaseUser = auth.getCurrentUser();                            assert firebaseUser != null;                            String userid = firebaseUser.getUid();                            // Сохраняем в Realtime Database                            reference = FirebaseDatabase.getInstance().getReference("Users").child(userid);                            HashMap<String, String> hashMap = new HashMap<>();                            hashMap.put("id", userid);                            hashMap.put("username", username);                            hashMap.put("imageURL", "default");                            // Добавим lowerCase имя для поиска в будущем                            hashMap.put("search", username.toLowerCase());                            hashMap.put("status", "offline");                            reference.setValue(hashMap).addOnCompleteListener(new OnCompleteListener<Void>() {                                @Override                                public void onComplete(@NonNull Task<Void> task) {                                    if (task.isSuccessful()){                                        // Переход в MainActivity                                        Intent intent = new Intent(requireActivity(), MainActivity.class);                                        // Очищаем стек, чтобы кнопка "Назад" не вернула нас на регистрацию                                        intent.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TASK | Intent.FLAG_ACTIVITY_NEW_TASK);                                        startActivity(intent);                                        requireActivity().finish();                                    }                                }                            });                        } else {                            // Выводим ошибку (например, "Email уже занят")                            String error = task.getException() != null ? task.getException().getMessage() : "Ошибка регистрации";                            Toast.makeText(getContext(), error, Toast.LENGTH_SHORT).show();                        }                    }                });    }}